import React from 'react';
import { Document, Page, Text, View, StyleSheet } from '@react-pdf/renderer';

const ModernTemplate = ({ data, customization }) => {
  const styles = StyleSheet.create({
    page: {
      padding: 30,
      fontFamily: customization.font || 'Helvetica',
      display: 'flex',
      flexDirection: 'row'
    },
    sidebar: {
      width: '30%',
      backgroundColor: customization.primaryColor || '#333333',
      padding: 20,
      color: 'white'
    },
    main: {
      width: '70%',
      padding: 20
    },
    name: {
      fontSize: 24,
      fontWeight: 'bold',
      marginBottom: 20
    },
    contact: {
      fontSize: 12,
      marginBottom: 5
    },
    section: {
      marginBottom: 20
    },
    sectionTitle: {
      fontSize: 16,
      fontWeight: 'bold',
      marginBottom: 10,
      color: customization.secondaryColor || '#f50057'
    },
    item: {
      marginBottom: 10
    },
    itemTitle: {
      fontSize: 14,
      fontWeight: 'bold'
    },
    itemSubtitle: {
      fontSize: 12,
      marginBottom: 5
    },
    itemContent: {
      fontSize: 10
    }
  });

  return (
    <Document>
      <Page size="A4" style={styles.page}>
        <View style={styles.sidebar}>
          <Text style={styles.name}>{data.firstName} {data.lastName}</Text>
          <View style={styles.section}>
            <Text style={styles.sectionTitle}>Contact</Text>
            <Text style={styles.contact}>{data.email}</Text>
            <Text style={styles.contact}>{data.phone}</Text>
          </View>
          
          {data.skills.length > 0 && (
            <View style={styles.section}>
              <Text style={styles.sectionTitle}>Skills</Text>
              {data.skills.map((skill, index) => (
                <Text key={index} style={styles.contact}>{skill}</Text>
              ))}
            </View>
          )}
        </View>
        
        <View style={styles.main}>
          {data.experience.length > 0 && (
            <View style={styles.section}>
              <Text style={styles.sectionTitle}>Experience</Text>
              {data.experience.map((exp, index) => (
                <View key={index} style={styles.item}>
                  <Text style={styles.itemTitle}>{exp.position}</Text>
                  <Text style={styles.itemSubtitle}>{exp.company} | {exp.startDate} - {exp.endDate}</Text>
                  <Text style={styles.itemContent}>{exp.description}</Text>
                </View>
              ))}
            </View>
          )}
          
          {data.education.length > 0 && (
            <View style={styles.section}>
              <Text style={styles.sectionTitle}>Education</Text>
              {data.education.map((edu, index) => (
                <View key={index} style={styles.item}>
                  <Text style={styles.itemTitle}>{edu.degree}</Text>
                  <Text style={styles.itemSubtitle}>{edu.school} | {edu.graduationDate}</Text>
                </View>
              ))}
            </View>
          )}
        </View>
      </Page>
    </Document>
  );
};

export default ModernTemplate;
