import React from 'react';
import { Document, Page, Text, View, StyleSheet } from '@react-pdf/renderer';

// Create styles
const styles = StyleSheet.create({
  page: {
    flexDirection: 'column',
    backgroundColor: '#FFFFFF',
    padding: 30,
  },
  header: {
    backgroundColor: '#2196f3',
    padding: 10,
    marginBottom: 10,
    color: 'white',
  },
  name: {
    fontSize: 24,
    textAlign: 'center',
    fontWeight: 'bold',
  },
  contact: {
    fontSize: 10,
    textAlign: 'center',
    marginTop: 5,
  },
  section: {
    margin: 10,
    padding: 10,
  },
  sectionTitle: {
    fontSize: 14,
    marginBottom: 10,
    fontWeight: 'bold',
    borderBottom: '1 solid #000',
    paddingBottom: 3,
  },
  entryTitle: {
    fontSize: 12,
    fontWeight: 'bold',
  },
  entrySubtitle: {
    fontSize: 10,
    marginBottom: 5,
  },
  entryDate: {
    fontSize: 10,
    fontStyle: 'italic',
  },
  entryDescription: {
    fontSize: 10,
    marginTop: 5,
  },
  skills: {
    flexDirection: 'row',
    flexWrap: 'wrap',
    marginTop: 5,
  },
  skill: {
    fontSize: 10,
    backgroundColor: '#f0f0f0',
    padding: '3 6',
    margin: 2,
    borderRadius: 3,
  },
});

const ProfessionalTemplate = ({ data, customization }) => (
  <Document>
    <Page size="A4" style={styles.page}>
      <View style={[styles.header, { backgroundColor: customization?.primaryColor || '#2196f3' }]}>
        <Text style={styles.name}>{data.firstName} {data.lastName}</Text>
        <Text style={styles.contact}>{data.email} | {data.phone}</Text>
      </View>
      
      {data.summary && (
        <View style={styles.section}>
          <Text style={styles.sectionTitle}>Professional Summary</Text>
          <Text style={styles.entryDescription}>{data.summary}</Text>
        </View>
      )}
      
      {data.experience.length > 0 && (
        <View style={styles.section}>
          <Text style={styles.sectionTitle}>Work Experience</Text>
          {data.experience.map((exp, index) => (
            <View key={index} style={{ marginBottom: 10 }}>
              <Text style={styles.entryTitle}>{exp.position}</Text>
              <Text style={styles.entrySubtitle}>{exp.company}</Text>
              <Text style={styles.entryDate}>{exp.startDate} - {exp.endDate}</Text>
              <Text style={styles.entryDescription}>{exp.description}</Text>
            </View>
          ))}
        </View>
      )}
      
      {data.education.length > 0 && (
        <View style={styles.section}>
          <Text style={styles.sectionTitle}>Education</Text>
          {data.education.map((edu, index) => (
            <View key={index} style={{ marginBottom: 10 }}>
              <Text style={styles.entryTitle}>{edu.degree}</Text>
              <Text style={styles.entrySubtitle}>{edu.school}</Text>
              <Text style={styles.entryDate}>{edu.graduationDate}</Text>
              <Text style={styles.entryDescription}>{edu.description}</Text>
            </View>
          ))}
        </View>
      )}
      
      {data.skills.length > 0 && (
        <View style={styles.section}>
          <Text style={styles.sectionTitle}>Skills</Text>
          <View style={styles.skills}>
            {data.skills.map((skill, index) => (
              <Text key={index} style={styles.skill}>{skill}</Text>
            ))}
          </View>
        </View>
      )}
    </Page>
  </Document>
);

export default ProfessionalTemplate;
