import React from 'react';
import { Document, Page, Text, View, StyleSheet } from '@react-pdf/renderer';

const CreativeTemplate = ({ data, customization }) => {
  const styles = StyleSheet.create({
    page: {
      padding: 30,
      fontFamily: customization.font || 'Helvetica'
    },
    header: {
      backgroundColor: customization.primaryColor || '#9c27b0',
      padding: 20,
      color: 'white',
      textAlign: 'center',
      marginBottom: 20,
      borderRadius: 5
    },
    name: {
      fontSize: 28,
      fontWeight: 'bold',
      marginBottom: 5
    },
    contact: {
      fontSize: 12,
      marginBottom: 10
    },
    section: {
      marginBottom: 20,
      padding: 15,
      backgroundColor: '#f9f9f9',
      borderRadius: 5
    },
    sectionTitle: {
      fontSize: 18,
      fontWeight: 'bold',
      marginBottom: 15,
      color: customization.secondaryColor || '#ff4081',
      textAlign: 'center',
      borderBottom: '2 dashed ' + (customization.secondaryColor || '#ff4081'),
      paddingBottom: 5
    },
    item: {
      marginBottom: 15
    },
    itemTitle: {
      fontSize: 16,
      fontWeight: 'bold',
      color: customization.primaryColor || '#9c27b0'
    },
    itemSubtitle: {
      fontSize: 14,
      marginBottom: 5,
      fontStyle: 'italic'
    },
    itemContent: {
      fontSize: 12
    },
    skillsContainer: {
      display: 'flex',
      flexDirection: 'row',
      flexWrap: 'wrap',
      justifyContent: 'center'
    },
    skill: {
      margin: 5,
      padding: '5 10',
      backgroundColor: customization.secondaryColor || '#ff4081',
      color: 'white',
      borderRadius: 10,
      fontSize: 10
    }
  });

  return (
    <Document>
      <Page size="A4" style={styles.page}>
        <View style={styles.header}>
          <Text style={styles.name}>{data.firstName} {data.lastName}</Text>
          <Text style={styles.contact}>{data.email} | {data.phone}</Text>
        </View>
        
        {data.experience.length > 0 && (
          <View style={styles.section}>
            <Text style={styles.sectionTitle}>Experience</Text>
            {data.experience.map((exp, index) => (
              <View key={index} style={styles.item}>
                <Text style={styles.itemTitle}>{exp.position}</Text>
                <Text style={styles.itemSubtitle}>{exp.company} | {exp.startDate} - {exp.endDate}</Text>
                <Text style={styles.itemContent}>{exp.description}</Text>
              </View>
            ))}
          </View>
        )}
        
        {data.education.length > 0 && (
          <View style={styles.section}>
            <Text style={styles.sectionTitle}>Education</Text>
            {data.education.map((edu, index) => (
              <View key={index} style={styles.item}>
                <Text style={styles.itemTitle}>{edu.degree}</Text>
                <Text style={styles.itemSubtitle}>{edu.school} | {edu.graduationDate}</Text>
              </View>
            ))}
          </View>
        )}
        
        {data.skills.length > 0 && (
          <View style={styles.section}>
            <Text style={styles.sectionTitle}>Skills</Text>
            <View style={styles.skillsContainer}>
              {data.skills.map((skill, index) => (
                <Text key={index} style={styles.skill}>{skill}</Text>
              ))}
            </View>
          </View>
        )}
      </Page>
    </Document>
  );
};

export default CreativeTemplate;
