import React from 'react';
import { Document, Page, Text, View, StyleSheet } from '@react-pdf/renderer';

// Create styles
const styles = StyleSheet.create({
  page: {
    flexDirection: 'row',
    backgroundColor: '#FFFFFF',
  },
  sidebar: {
    width: '30%',
    backgroundColor: '#333333',
    padding: 20,
    color: 'white',
  },
  main: {
    width: '70%',
    padding: 20,
  },
  name: {
    fontSize: 24,
    fontWeight: 'bold',
    marginBottom: 10,
  },
  contact: {
    fontSize: 10,
    marginBottom: 20,
  },
  sectionTitle: {
    fontSize: 14,
    marginBottom: 10,
    fontWeight: 'bold',
    color: '#f50057',
  },
  mainSectionTitle: {
    fontSize: 14,
    marginBottom: 10,
    fontWeight: 'bold',
    borderBottom: '1 solid #ddd',
    paddingBottom: 3,
  },
  entryTitle: {
    fontSize: 12,
    fontWeight: 'bold',
  },
  entrySubtitle: {
    fontSize: 10,
    marginBottom: 5,
  },
  entryDate: {
    fontSize: 10,
    fontStyle: 'italic',
  },
  entryDescription: {
    fontSize: 10,
    marginTop: 5,
    marginBottom: 10,
  },
  skills: {
    marginTop: 5,
  },
  skill: {
    fontSize: 10,
    marginBottom: 5,
  },
});

const ModernTemplate = ({ data, customization }) => (
  <Document>
    <Page size="A4" style={styles.page}>
      <View style={[styles.sidebar, { backgroundColor: customization?.primaryColor || '#333333' }]}>
        <Text style={styles.name}>{data.firstName} {data.lastName}</Text>
        <Text style={styles.contact}>{data.email}</Text>
        <Text style={styles.contact}>{data.phone}</Text>
        
        {data.skills.length > 0 && (
          <View style={{ marginTop: 20 }}>
            <Text style={[styles.sectionTitle, { color: customization?.secondaryColor || '#f50057' }]}>Skills</Text>
            <View style={styles.skills}>
              {data.skills.map((skill, index) => (
                <Text key={index} style={styles.skill}>â€¢ {skill}</Text>
              ))}
            </View>
          </View>
        )}
      </View>
      
      <View style={styles.main}>
        {data.summary && (
          <View style={{ marginBottom: 20 }}>
            <Text style={styles.mainSectionTitle}>Professional Summary</Text>
            <Text style={styles.entryDescription}>{data.summary}</Text>
          </View>
        )}
        
        {data.experience.length > 0 && (
          <View style={{ marginBottom: 20 }}>
            <Text style={styles.mainSectionTitle}>Work Experience</Text>
            {data.experience.map((exp, index) => (
              <View key={index} style={{ marginBottom: 10 }}>
                <Text style={styles.entryTitle}>{exp.position}</Text>
                <Text style={styles.entrySubtitle}>{exp.company}</Text>
                <Text style={styles.entryDate}>{exp.startDate} - {exp.endDate}</Text>
                <Text style={styles.entryDescription}>{exp.description}</Text>
              </View>
            ))}
          </View>
        )}
        
        {data.education.length > 0 && (
          <View>
            <Text style={styles.mainSectionTitle}>Education</Text>
            {data.education.map((edu, index) => (
              <View key={index} style={{ marginBottom: 10 }}>
                <Text style={styles.entryTitle}>{edu.degree}</Text>
                <Text style={styles.entrySubtitle}>{edu.school}</Text>
                <Text style={styles.entryDate}>{edu.graduationDate}</Text>
                <Text style={styles.entryDescription}>{edu.description}</Text>
              </View>
            ))}
          </View>
        )}
      </View>
    </Page>
  </Document>
);

export default ModernTemplate;
